import React, { Component } from "react";
import { getMovies, deleteMovie, getMovie } from "../services/fakeMovieService";

class Movies extends Component {
  state = {
    movies: JSON.parse(JSON.stringify(getMovies())),
    likedMovies: []
  };

  componentDidUpdate(prevProps, prevState) {
    console.log(prevState.movies);
  }

  render() {
    return <div className="container">{this.renderTable()}</div>;
  }

  renderTable() {
    return this.state.movies.length === 0 ? (
      <h1>No movies in the basket</h1>
    ) : (
      <React.Fragment>
        <h1>There are {this.state.movies.length} movies in the basket</h1>
        <table className="table">
          <thead>
            <tr>
              <th>Title</th>
              <th>Genre</th>
              <th>Stock</th>
              <th>Rate</th>
              <th />
              <th />
            </tr>
          </thead>
          <tbody>{this.renderMovieList()}</tbody>
        </table>
      </React.Fragment>
    );
  }

  addToFavs(id) {
    const movie = getMovie(id);

    this.setState({
      likedMovies: this.state.likedMovies.concat()
    });
  }

  renderMovieList() {
    return this.state.movies.map(movie => (
      <tr key={movie._id}>
        <td>{movie.title}</td>
        <td>{movie.genre.name}</td>
        <td>{movie.numberInStock}</td>
        <td>{movie.dailyRentalRate}</td>
        <td>
          <i className="fa fa-heart-o" aria-hidden="true" />
        </td>
        <td>
          <button
            className="btn btn-danger btn-md"
            onClick={() => {
              deleteMovie(movie._id);
              this.setState({
                movies: JSON.parse(JSON.stringify(getMovies()))
              });
            }}
          >
            Delete
          </button>
        </td>
      </tr>
    ));
  }
}

export default Movies;
