import React, { Component } from "react";
import Counter from "../components/counter";

class Counters extends Component {
  state = {
    counters: [
      { id: 1, value: 0 },
      { id: 2, value: 10 },
      { id: 3, value: 5 },
      { id: 4, value: 4 }
    ]
  };

  render() {
    return (
      <React.Fragment>
        <button className="btn btn-success btn-md" onClick={this.addCounter}>
          Add
        </button>
        <button className="btn btn-warning btn-md" style={{ marginLeft: "2%" }}>
          Reset
        </button>
        {this.state.counters.map(counter => (
          <Counter
            key={counter.id}
            onDelete={this.handleDelete}
            counter={counter}
          />
        ))}
      </React.Fragment>
    );
  }

  handleDelete = counterID => {
    this.setState({
      counters: this.state.counters.filter(counter => counter.id !== counterID)
    });
  };

  addCounter = () => {
    const ids = this.state.counters.map(counter => counter.id);
    const maxID = Math.max(...ids) + 1;
    this.setState({
      counters: this.state.counters.concat({ id: maxID, value: 0 })
    });
  };
}

export default Counters;
