import React, { Component } from "react";

export default class Counter extends Component {
  // Class state variables
  state = {
    count: 0,
    tags: ["Tag1", "Tag2", "Tag3"]
  };

  // Default construtor
  constructor() {
    super();
    this.handleIncrement.bind(this);
  }

  // Define styles
  styles = {
    fontSize: 15,
    fontWeight: "bold"
  };

  // Class render method
  render() {
    return (
      <React.Fragment>
        <span style={this.styles} className={this.selectBadgeClass()}>
          {this.formatNumber()}
        </span>
        <button
          className="btn btn-secondary btn-sm"
          onClick={this.handleIncrement}
        >
          Increment
        </button>
        {this.state.tags.length === 0 && (
          <p class="m-2">Please enter atleast one tag!!</p>
        )}
        {this.renderTags()};
      </React.Fragment>
    );
  }

  // Increment the counter
  handleIncrement() {
    console.log("Increment +1");
  }

  // Method to render tags
  renderTags() {
    return this.state.tags.length === 0 ? (
      <p class="m-2">There are no tags!!</p>
    ) : (
      <ul>
        {this.state.tags.map(tag => (
          <li key={tag}>{tag}</li>
        ))}
      </ul>
    );
  }

  // Define badge classes
  selectBadgeClass() {
    let badgeClasses = "badge m-2 badge-";
    badgeClasses += this.state.count === 0 ? "warning" : "primary";
    return badgeClasses;
  }

  // Format number method
  formatNumber() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count;
  }
}
