import React, { Component } from "react";

export default class Counter extends Component {
  // Class state variables
  state = {
    count: 0,
    tags: ["tag1", "tag2", "tag3"]
  };

  // Define styles
  styles = {
    fontSize: 15,
    fontWeight: "bold"
  };

  // Class render method
  render() {
    return (
      <React.Fragment>
        <span style={this.styles} className={this.selectBadgeClass()}>
          {this.formatNumber()}
        </span>
        <button className="btn btn-secondary btn-sm">Increment</button>
        {this.renderTags()};
      </React.Fragment>
    );
  }

  // Method to render tags
  renderTags() {
    return this.state.tags.length === 0 ? (
      <p>There are no tags!!</p>
    ) : (
      <ul>
        {this.state.tags.map(tag => (
          <li key={tag}>{tag}</li>
        ))}
      </ul>
    );
  }

  // Define badge classes
  selectBadgeClass() {
    let badgeClasses = "badge m-2 badge-";
    badgeClasses += this.state.count === 0 ? "warning" : "primary";
    return badgeClasses;
  }

  // Format number method
  formatNumber() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count;
  }
}
