import React, { Component } from "react";
import { getMovies, deleteMovie } from "../services/fakeMovieService";

class Movies extends Component {
  state = {
    movies: JSON.parse(JSON.stringify(getMovies())),
    likedMovies: []
  };

  componentDidUpdate(prevProps, prevState) {
    console.log(prevState.likedMovies);
  }

  render() {
    return <div className="container">{this.renderTable()}</div>;
  }

  renderTable() {
    return this.state.movies.length === 0 ? (
      <h1>No movies in the basket</h1>
    ) : (
      <React.Fragment>
        <h1>There are {this.state.movies.length} movies in the basket</h1>
        <table className="table customTable">
          <thead>
            <tr>
              <th>Title</th>
              <th>Genre</th>
              <th>Stock</th>
              <th>Rate</th>
              <th />
              <th />
            </tr>
          </thead>
          <tbody>{this.renderMovieList()}</tbody>
        </table>
      </React.Fragment>
    );
  }

  addRemoveFromFavs(id) {
    this.setState({
      likedMovies: this.state.likedMovies.concat(id)
    });
  }

  renderLikeIcon(id) {
    return this.state.likedMovies.indexOf(id) !== -1
      ? "fa fa-heart"
      : "fa fa-heart-o";
  }

  handleDelete(id) {
    deleteMovie(id);
    this.setState({
      movies: JSON.parse(JSON.stringify(getMovies())),
      likedMovies: this.state.likedMovies.filter(
        likedMovie => likedMovie.indexOf(id) === -1
      )
    });
  }

  renderMovieList() {
    return this.state.movies.map(movie => (
      <tr key={movie._id}>
        <td>{movie.title}</td>
        <td>{movie.genre.name}</td>
        <td>{movie.numberInStock}</td>
        <td>{movie.dailyRentalRate}</td>
        <td>
          <i
            className={this.renderLikeIcon(movie._id)}
            aria-hidden="true"
            onClick={() => this.addToFavs(movie._id)}
          />
        </td>
        <td>
          <button
            className="btn btn-danger btn-md"
            onClick={() => {
              this.handleDelete(movie._id);
            }}
          >
            Delete
          </button>
        </td>
      </tr>
    ));
  }
}

export default Movies;
