import React, { Component } from "react";

export default class Counter extends Component {
  styles = {
    fontSize: "0.8rem",
    fontWeight: "bold",
    verticalAlign: "middle",
    width: "3rem"
  };

  render() {
    const { onIncrement, onDecrement, onDelete } = this.props;
    const { id, value } = this.props.counter;

    return (
      <div style={{ marginTop: "1%" }}>
        <button
          className="btn btn-secondary btn-sm"
          onClick={() => {
            onDecrement(id);
          }}
        >
          Decrement
        </button>
        <span style={this.styles} className={selectBadgeClass(value)}>
          {this.formatNumber(value)}
        </span>
        <button
          className="btn btn-secondary btn-sm"
          onClick={() => {
            onIncrement(id);
          }}
        >
          Increment
        </button>
        <button
          className="btn btn-danger btn-sm m-2"
          onClick={() => {
            onDelete(id);
          }}
        >
          Delete
        </button>
      </div>
    );
  }

  selectBadgeClass(value) {
    let badgeClasses = "badge m-2 badge-";
    badgeClasses += value === 0 ? "warning" : "primary";
    return badgeClasses;
  }

  formatNumber(value) {
    return value === 0 ? "Zero" : value;
  }
}
